import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import { motion } from 'framer-motion';
import "react-responsive-carousel/lib/styles/carousel.min.css";

const Home = () => {
  return (
    <div className="home">
      <motion.header 
        className="hero"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <h1>Experts en Rénovation</h1>
        <p>Donnez vie à vos espaces</p>
      </motion.header>

      <motion.section 
        className="carousel-section"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
      >
        <h2>Nos Réalisations</h2>
        <Carousel autoPlay infiniteLoop showThumbs={false}>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Projet 1" />
            <p className="legend">Salle de bain moderne</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Projet 2" />
            <p className="legend">Cuisine rénovée</p>
          </div>
        </Carousel>
      </motion.section>
    </div>
  );
};

export default Home;
